SELECT EmployeeId AS EmployeeId,Lang AS Lang,FirstName AS FirstName,LastName AS LastName,Division AS Division,DateOfBirth AS DateOfBirth,PlaceOfBirth AS PlaceOfBirth,Religion AS Religion,Gender AS Gender,MaritalStatus AS MaritalStatus,Email AS Email FROM hr_mstr_employee LEFT JOIN hr_users ON hr_mstr_employee.EmployeeId=hr_users.EmployeeId WHERE 1=1    ORDER BY EmployeeId ASC LIMIT 0, 5