SELECT nim_mahasiswa AS nim_mahasiswa,nama_mahasiswa AS nama_mahasiswa,jenis_kelamin_mahasiswa AS jenis_kelamin_mahasiswa,jurusan_mahasiswa AS jurusan_mahasiswa,fakultas_mahasiswa AS fakultas_mahasiswa,0 AS TempatTanggalLahirMahasiswa,alamat_mahasiswa AS alamat_mahasiswa,no_hp_mahasiswa AS no_hp_mahasiswa,email_mahasiswa AS email_mahasiswa FROM tbl_mahasiswa WHERE 1=1    ORDER BY Periode ASC LIMIT 0, 5