SELECT id_mahasiswa AS id_mahasiswa,IdUser AS IdUser,nim_mahasiswa AS nim_mahasiswa,nama_mahasiswa AS nama_mahasiswa,jenis_kelamin_mahasiswa AS jenis_kelamin_mahasiswa,jurusan_mahasiswa AS jurusan_mahasiswa,tempat_lahir_mahasiswa AS tempat_lahir_mahasiswa,tanggal_lahir_mahasiswa AS tanggal_lahir_mahasiswa,alamat_mahasiswa AS alamat_mahasiswa,no_hp_mahasiswa AS no_hp_mahasiswa,email_mahasiswa AS email_mahasiswa,tahun_masuk_mahasiswa AS tahun_masuk_mahasiswa,tahun_lulus_mahasiswa AS tahun_lulus_mahasiswa,status_mahasiswa AS status_mahasiswa FROM tbl_mahasiswa WHERE 1=1  AND (  UPPER(id_mahasiswa)  LIKE  UPPER('%lilis%')  OR  UPPER(IdUser)  LIKE  UPPER('%lilis%')  OR  UPPER(nim_mahasiswa)  LIKE  UPPER('%lilis%')  OR  UPPER(nama_mahasiswa)  LIKE  UPPER('%lilis%')  OR  UPPER(jenis_kelamin_mahasiswa)  LIKE  UPPER('%lilis%')  OR  UPPER(jurusan_mahasiswa)  LIKE  UPPER('%lilis%')  OR  UPPER(tempat_lahir_mahasiswa)  LIKE  UPPER('%lilis%')  OR  UPPER(tanggal_lahir_mahasiswa)  LIKE  UPPER('%lilis%')  OR  UPPER(alamat_mahasiswa)  LIKE  UPPER('%lilis%')  OR  UPPER(no_hp_mahasiswa)  LIKE  UPPER('%lilis%')  OR  UPPER(email_mahasiswa)  LIKE  UPPER('%lilis%')  OR  UPPER(tahun_masuk_mahasiswa)  LIKE  UPPER('%lilis%')  OR  UPPER(tahun_lulus_mahasiswa)  LIKE  UPPER('%lilis%')  OR  UPPER(status_mahasiswa)  LIKE  UPPER('%lilis%')  )   ORDER BY nim_mahasiswa ASC LIMIT 0, 5