SELECT id_mahasiswa AS id_mahasiswa,IdUser AS IdUser,nim_mahasiswa AS nim_mahasiswa,nama_mahasiswa AS nama_mahasiswa,jenis_kelamin_mahasiswa AS jenis_kelamin_mahasiswa,jurusan_mahasiswa AS jurusan_mahasiswa,tempat_lahir_mahasiswa AS tempat_lahir_mahasiswa,tanggal_lahir_mahasiswa AS tanggal_lahir_mahasiswa,alamat_mahasiswa AS alamat_mahasiswa,no_hp_mahasiswa AS no_hp_mahasiswa,email_mahasiswa AS email_mahasiswa,tahun_masuk_mahasiswa AS tahun_masuk_mahasiswa,tahun_lulus_mahasiswa AS tahun_lulus_mahasiswa,status_mahasiswa AS status_mahasiswa FROM tbl_mahasiswa WHERE 1=1  AND (status_mahasiswa='ACTIVE')    ORDER BY nim_mahasiswa ASC LIMIT 0, 5